import './ExpensesFilter.css';

const ExpensesFilter = (props) => {
  const saveSelectedYear = (event) =>{
    props.onSelectedYear(event.target.value);
  };

  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select>
          <option value='2022' onUpdate={saveSelectedYear}>2022</option>
          <option value='2021' onChange={saveSelectedYear}>2021</option>
          <option value='2020' onChange={saveSelectedYear}>2020</option>
          <option value='2019' onChange={saveSelectedYear}>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
